import React from 'react';
import { Paper, Container, Text, Title, Anchor } from '@mantine/core';
import { fetchReps, fetchTeams, fetchSalesOpps } from '../api';

const LandingPage = () => {
    const [reps, setReps] = React.useState({});
    const [teams, setTeams] = React.useState({});
    const [salesOppsMapByRepId, setSalesOppsMapByRepId] = React.useState({});

    const getData = async () => {
        const [repData, teamData, salesOppsData] = await Promise.all([fetchReps(), fetchTeams(), fetchSalesOpps()])
        setReps(repData);
        setTeams(teamData);
        const opsByRepId = salesOppsData.reduce((acc, opp) => {
            if (!acc[opp.repId]) {
                acc[opp.repId] = opp.revenue;
            }
            acc[opp.repId] += opp.revenue;
            return acc;
        }, {});
    }

    React.useEffect(() => { }, []);
    return (
        <Paper shadow="md" p="xl">
            <Container size="md">
                <Title order={1}>Good luck!</Title>
                <Text>
                    Please refer to read me or reach out with any questions
                </Text>
                <Text>
                    It is recomended to use the Mantine Components&nbsp;&nbsp;
                    <Anchor href="https://mantine.dev/" target="_blank">
                        Mantine docs
                    </Anchor>
                </Text>
            </Container>
        </Paper>
    );
};

export default LandingPage;
